int index_(char line[], char key[]) {
	int len = strlen(key);
	int total_len = strlen(line);
	for (int i = 0; i <= total_len - len; ++ i) {
		if (strncmp(line + i, key, len) == 0) return i;
	}
	return -1;
}

int atoi_(char word[]) {
	int len = strlen(word);
	int value = 0;
	for (int i = 0; i < len; ++ i) {
		value = value * 10 + word[i] - '0';
	}
	return value;
}

int get_int(char line[], char key[]) {
	int start = index_(line, key);
	if (start == -1) return 0;
	int size;
	int index = int_index(line + start, &size);
	if (index == -1) {
		puts("No number");
		return 0;
	}
	char c = line[start + index + size];
	line[start + index + size] = 0;
	int val = atoi_(line + start + index);
	line[start + index + size] = c;
	return val;
}

