int computed[1000] = {0};

int getMaxCuttingPrice(int prices[][2], int priceCount, int rodLength) {
	if (computed[rodLength] > 0) return computed[rodLength];
	int cur_max = getPrice(prices, priceCount, rodLength);
	for (int i = 1; i < rodLength; ++ i) {
		int price = getMaxCuttingPrice(prices, priceCount, i) +
					getMaxCuttingPrice(prices, priceCount, rodLength - i);
		cur_max = (price > cur_max ? price : cur_max);
	}
	computed[rodLength] = cur_max;
	return cur_max;
}

