		return getEmptyResult();
	}

	if (text1[0] == text2[0]) {
		struct Result result = LCS(&text1[1], &text2[1]);
		return insertAt0(result, text1[0]);
	}

	struct Result result1 = LCS(&text1[1], text2);
	struct Result result2 = LCS(text1, &text2[1]);
	return getBetter(result1, result2);
}

