void initialize2(int mul[][100], int first, int len, int dim[]) {
	int last = first + len - 1;
	// for all possible sub-sequences
	int total = 0;
	for (int mid = first; mid < last; ++ mid) {
		int total1 = mul[first][mid] + mul[mid + 1][last] +
		dim[first] * dim[mid + 1] * dim[last + 1];
		if (total == 0 || total > total1) total = total1;
	}
	mul[first][last] = total;
}

void initialize(int mul[][100], int matrixes, int dim[]) {
	// all sequence of length 1
	for (int i = 0; i < matrixes; ++ i) {
		mul[i][i] = 0;
	}

	// from short to long sequence
	for (int len = 2; len <= matrixes; ++ len) {
		for (int first = 0; first <= matrixes - len; ++ first) {
			initialize2(mul, first, len, dim);
		}
	}
}

int getMultiplications(int matrixes, int dimensions[]) {
	int multiplications[100][100];
	initialize(multiplications, matrixes, dimensions);
	return multiplications[0][matrixes - 1];
}

