int knapsack(int p[], int w[], int maxWeight) {
	int price[100]; // assume that maxWeight < 100
	price[0] = 0;

	for (int weight = 1; weight <= maxWeight; ++ weight) {
		price[weight] = price[weight - 1];
		for (int item = 0; item < 10; ++ item) {
			int weight1 = weight - w[item];
			if (weight1 < 0) continue;
			int price1 = p[item] + price[weight1];
			if (price[weight] < price1) {
				price[weight] = price1;
			}
		}
	}

	return price[maxWeight];
}

