int knapsack(int p[], int w[], int maxWeight) {
	int A[11][100];
	for (int j = 0; j < 11; ++ j) A[j][0] = 0;
	for (int y = 0; y <= maxWeight; ++ y) A[0][y] = 0;
	for (int y = 1; y <= maxWeight; ++ y) {
		for (int j = 1; j < 11; ++ j) {
			A[j][y] = A[j-1][y];
			if (w[j-1] <= y) {
				int p2 = p[j-1] + A[j-1][y-w[j-1]];
				A[j][y] = (A[j][y] > p2 ? A[j][y] : p2);
			}
		}
	}
	return A[10][maxWeight];
}

