void swap(double * value1, double * value2) {
	double temp = *value1;
	*value1 = *value2;
	*value2 = temp;
}

void sort(double * value1, double * value2, double * value3) {
	if (*value1 > *value2) swap(value1, value2);
	if (*value2 > *value3) swap(value2, value3);
	if (*value1 > *value2) swap(value1, value2);
}

