};

void read(struct Goods * goods) {
	scanf("%d %d %d\n", &(*goods).number, 
			&(*goods).weight, &(*goods).price);
}

void readArray(struct Goods goods[], int size) {
	for (int i = 0; i < size; ++ i) {
		read(&goods[i]);
	}
}

