	sort(addresses, 10);

	printArray(goods, 10);
	printf("\n");
	printGoods(addresses, 10);
}

int getMaxPriceIndex(const struct Goods * addresses[], int size) {
	int maxPrice = (*addresses[0]).price;
	int index = 0;
	for (int i = 1; i < size; ++ i) {
		int price = (*addresses[i]).price;
		if (maxPrice < price) {
			maxPrice = price;
			index = i;
		}
	}
	return index;
}

void swap(const struct Goods * * addr1, const struct Goods * * addr2) {
	const struct Goods * temp = * addr1;
	* addr1 = * addr2;
	* addr2 = temp;
}

void sort(const struct Goods * addresses[], int size) {
	for (int i = size; i > 1; -- i) {
		int index = getMaxPriceIndex(addresses, i);
		if (index == i - 1) continue;
		else swap(&addresses[i - 1], &addresses[index]);
	}
}

