#include <stdio.h>
#include <stdlib.h>

enum Format { CHINESE, MONTH_FIRST, YEAR_FIRST, WRONG };

struct Date
{
	int year;
	int month;
	int day;
	enum Format format;
};

void printDate(struct Date date) {
	switch (date.format) {
	case CHINESE: printf("%d年%d月%d日\n", date.year, date.month, date.day); break;
	case MONTH_FIRST: printf("%d/%d/%d\n", date.month, date.day, date.year); break;
	case YEAR_FIRST: printf("%d/%d/%d\n", date.year, date.month, date.day); break;
	default: puts("Wrong format");
	}
}

#include "source.c"

int main() {
	for (int i = 0; i < 4; ++ i) {
		struct Date date = readDate();
		printDate(date);
	}
}

struct Date readDate() {
	struct Date date;
	char line[100];
	fgets(line, 100, stdin);
	if (sscanf(line, "%d年%d月%d日\n", &date.year, &date.month, &date.day) == 3) {
		date.format = CHINESE;
	}
	else if (sscanf(line, "%d/%d/%d", &date.month, &date.day, &date.year) == 3) {
		date.format = MONTH_FIRST;
	}
	else if (sscanf(line, "%d-%d-%d", &date.year, &date.month, &date.day) == 3) {
		date.format = YEAR_FIRST;
	}
	else {
		date.format = WRONG;
	}
	return date;
}

