#include <stdio.h>
#include <string.h>

enum Status { SPACE, NON_SPACE };

int is_space(char c) {
	return c == ' ' || c == '\t' || c == '\r' || c == '\n';
}

int split_words(char line[], char * words[], int max_words) {
	enum Status status = SPACE;
	int word_count = 0;
	int len = strlen(line);
	for (int i = 0; i < len; ++ i) {
		if (status == NON_SPACE) {
			if (is_space(line[i])) {
				line[i] = 0;
				status = SPACE;
			}
		}
		else { // status == SPACE
			if (! is_space(line[i])) {
				words[word_count ++ ] = line + i;
				status = NON_SPACE;
			}
		}
	}
	return word_count;
}

int read_ints(double values[], int max_size) {
	char line[1000];
	if (fgets(line, 100, stdin) == NULL) return 0;
	char * words[100];
	int word_count = split_words(line, words, 100);
	for (int i = 0; i < word_count; ++ i)
		sscanf(words[i], "%lg", &values[i]);
	return word_count;
}

