int is_leap_year(int year) {
	return (year % 4 == 0 && year % 100 != 0) || (year % 400 == 0);
}

int reverse(int n) {
	int r = 0;
	while (n > 0) {
		r = r * 10 + (n % 10);
		n /= 10;
	}
	return r;
}

int is_prime(int number) {
	int i;
	for (i = number / 2; i >= 2; -- i) {
		if (number % i == 0) return 0;
	}
	return 1;
}

