int power(int base, int exp) {
	int pow = 1;
	int i;
	for (i = 0; i < exp; ++ i) {
		pow *= base;
	}
	return pow;
}

int num_length(int num) {
	int len = 1;
	int i;
	for (i = 0; i < 100; ++ i) {
		if (num < power(10, i + 1)) return len;
		++ len;
	}
	return 0;
}

void progress_bar(int total_tasks, int cur_task, int progress_bar_length) {
	int percentage = cur_task * 100 / total_tasks;
	int label_length = num_length(percentage) + 1;
	int label_start = (progress_bar_length - label_length) / 2;
	int num_equals = cur_task * (progress_bar_length - 2) / total_tasks;

	int i;
	for (i = 0; i < progress_bar_length; ++ i) {
		if (i == 0) putchar('[');
		else if (i == progress_bar_length - 1) putchar(']');
		else if (i == label_start) printf("%d%%", percentage);
		else if (i < label_start || i >= label_start + label_length) {
			if (i - 1 < num_equals) putchar('=');
			else if (i - 1 == num_equals) putchar('>');
			else putchar(' ');
		}
	}
}

int main() {
	int total_tasks = get_int();
	int cur_task;
	int progress_bar_length = get_int();
	for (cur_task = 0; cur_task <= total_tasks; ++ cur_task) {
		progress_bar(total_tasks, cur_task, progress_bar_length);
		putchar('\n');
	}
}

