int find(int number, int array[], int size) {
	for (int i = 0; i < size; ++ i) {
		if (number == array[i]) return i;
	}
	return -1;
}

int getOccurrenceTimes(int array[], int size, int numbers[], int occurrences[]) {
	int numbersCount = 0;
	for (int i = 0; i < size; ++ i) {
		int index = find(array[i], numbers, numbersCount);
		if (index == -1) {
			numbers[numbersCount] = array[i];
			occurrences[numbersCount] = 1;
			++ numbersCount;
		}
		else {
			++ occurrences[index];
		}
	}
	return numbersCount;
}

int getFirstOccurrenceIndexes(int array[], int size, int numbers[], int indexes[]) {
	int numbersCount = 0;
	for (int i = 0; i < size; ++ i) {
		int index = find(array[i], numbers, numbersCount);
		if (index == -1) {
			numbers[numbersCount] = array[i];
			indexes[numbersCount] = i;
			++ numbersCount;
		}
	}
	return numbersCount;
}

void getSortedIndexes(const int array[], int size, int indexes[]) {
	for (int i = 0; i < size; ++ i) {
		indexes[i] = i;
	}
	for (int i = size - 1; i > 0; -- i) {
		for (int j = 0; j < i; ++ j) {
			int index1 = indexes[j];
			int index2 = indexes[j + 1];
			if (array[index1] > array[index2]) {
				indexes[j] = index2;
				indexes[j + 1] = index1;
			}
		}
	}
}

