// numberOfWays : dynamic programming helper funtion
int numberOfWays0(int totalSteps, int stepSizes[], int stepSizesCount, int memorized[]) {
	if (memorized[totalSteps] != -1) return memorized[totalSteps];

	int ways = 0;
	for (int i = 0; i < stepSizesCount; ++ i) {
		int firstStepSize = stepSizes[i];
		int remainingSteps = totalSteps - firstStepSize;
		if (remainingSteps < 0) continue;
		if (remainingSteps == 0) {
			++ ways;
			continue;
		} 
		ways += numberOfWays0(remainingSteps, stepSizes, stepSizesCount, memorized);
	}

	memorized[totalSteps] = ways;
	return ways;
}

// numberOfWays
int numberOfWays(int totalSteps, int stepSizes[], int stepSizesCount) {
	int memorized[100];
	for (int i = 0; i < totalSteps + 1; ++ i) {
		memorized[i] = -1;
	}
	int ways = numberOfWays0(totalSteps, stepSizes, stepSizesCount, memorized);
	return ways;
}

