#include <stdio.h>

void print_line1(int r, int h) {
	if (r < h / 2) {
		for (int i = 0; i < h; ++ i) {
			if (i < h / 2) putchar('-');
			else putchar('|');
		}
	}
	else {
		for (int i = 0; i < h; ++ i) {
			if (i < h / 2) putchar('|');
			else putchar('-');
		}
	}
}

void print_line2(int r, int h) {
	if (r < h / 2) {
		for (int i = 0; i < h; ++ i) {
			if (i < r || i >= h-r-1) putchar('/');
			else putchar('\\');
		}
	}
	else {
		for (int i = 0; i < h; ++ i) {
			if (i < h-r || i > r) putchar('/');
			else putchar('\\');
		}
	}
}

void print_line(int r, int h) {
	print_line1(r, h);
	print_line2(r, h);
	putchar('\n');
}

int main() {
	int h;
	scanf("%d", &h);
	for (int i = 0; i < h; ++ i) {
		print_line(i, h);
	}
}

