int get_line(char buf[], int max) {
	int i = 0;
	for (i = 0; i < max - 1; ++ i) {
		int c = getchar();
		if (c == -1 || c == '\n' || c == '\r') break;
		buf[i] = c;
	}
	buf[i] = 0;
	return i;
}

/*
Each `words[i]' will point to the first character of the i-th word
`count' will be the total number of words in `line' 
*/
int set_pointers_to_words(char line[], char * words[], int max) {
	int count = 0;
	for (int i = 0; line[i] != 0 && count < max; ++ i) {
		if (line[i] != ' ' && (i == 0 || line[i - 1] == ' '))
			words[count ++] = line + i;
	}
	return count;
}

/*
Print a number of `num_words' words
Each `words[i]' points to the first charater of the i-th word
*/
void print_words(char * words[], int num_words) {
	for (int i = 0; i < num_words; ++ i) {
		if (i > 0) putchar(' ');
		char * w = words[i];
		while (*w != 0 && *w != ' ') putchar(*w++);
	}
	putchar('\n');
}

void test() {
	char line[100];
	get_line(line, 100);
	char * words[10];
	int num_words = set_pointers_to_words(line, words, 10);
	sort_words(words, num_words);
	print_words(words, num_words);
}

int main() {
	for (int i = 0; i < 10; ++ i) 
		test();
}

int compare(char * s1, char * s2) {
	for (int i = 0; ; ++ i) {
		char c1 = s1[i];
		char c2 = s2[i];
		if (c1 == ' ') c1 = 0;
		if (c2 == ' ') c2 = 0;
		if (c1 > c2) return 1;
		if (c1 < c2) return -1;
		if (c1 == 0) return 0;
	}
}

void sort_words(char * words[], int num_words) {
	for (int i = 0; i < num_words - 1; ++ i) {
		for (int j = 0; j < num_words-1-i; ++ j) {
			if (compare(words[j], words[j+1]) > 0) {
				char * temp = words[j];
				words[j] = words[j+1];
				words[j+1] = temp;
			}
		}
	}
}

