#include <stdio.h>
#include <stdlib.h>

typedef struct {
	double ** array2;
	int height;
	int width;
} Matrix;

Matrix make2d(int height, int width) {
	Matrix m;
	m.height = height;
	m.width = width;
	m.array2 = (double **)malloc(height * sizeof(double*));
	for (int i = 0; i < height; ++ i)
		m.array2[i] = (double *)malloc(width * sizeof(double));
	return m;
}

void free2d(Matrix m) {
	for (int i = 0; i < m.height; ++ i) free(m.array2[i]);
	free(m.array2);
	m.array2 = 0;
}

