void fill2d(Matrix m, double val) {
	for (int i = 0; i < m.height; ++ i) {
		for (int j = 0; j < m.width; ++ j)
			m.array2[i][j] = val;
	}
}

void print2d(Matrix m) {
	for (int i = 0; i < m.height; ++ i) {
		for (int j = 0; j < m.width; ++ j)
			printf("%.2lf ", m.array2[i][j]);
		putchar('\n');
	}
}
