#include <vector>
using namespace std;
vector<string> split(const string & line) {
	int a = 0;
	vector<string> words;
	string b = " ";
	while (a < line.size()) {
		int end = line.find(b , a);
		if(end == a) a = a + b.size();
		else {
			if(end == -1) {
				words.push_back(line.substr(a));
				break;
			}
			words.push_back(line.substr(a , end - a));
			a = end + b.size();
		}

	}
	return words;
}

