}

void addFirst(const E & e)
    {
        Node<E> * node = new Node<E>;
        node -> element = e;
        node -> update_prev_and_next_node(nullptr, head);
        if (head != nullptr) head -> update_prev_node(nullptr, node);
        head = node;
        if (tail == nullptr) tail = node;
        ++_size;
    }

    void addLast(const E & e)
    {
        Node<E> * node = new Node<E>;
        node -> element = e;
        node -> update_prev_and_next_node(tail, nullptr);
        if (tail != nullptr) tail -> update_next_node(nullptr, node);
        tail = node;
        if (head == nullptr) head = node;
        ++_size;
    }

  void remove (int index) {

		if (index < 0 || index >= _size)
        throw NotSuchElementException();

        Node<E> * node =  get_node(index);
        Node<E> * nextofnode;
			Node<E> * preofnode;
        if(index == _size - 1)
        {
			nextofnode = 0;
		}
		else
		{
			nextofnode = get_node( index + 1 );
		}
        if (index == 0)
		{
			preofnode = 0;
		}
		else
		{
			preofnode = node->prev_node(nextofnode);
		}
        if(preofnode != 0)
        {
            preofnode->update_next_node(node,nextofnode );
        }
        if(nextofnode != 0)
        {
            nextofnode->update_prev_node(node, preofnode);
        }
        if(index == 0)
        {
            head = nextofnode;
        }
        if(index == _size - 1)
        {
            tail = preofnode;
        }
        delete node;
		-- _size;
	}


    void removeFirst()
    {
        remove(0);
    }

    void removeLast()
    {
        remove(_size - 1);
    }

   void removeFirstOccurrence(const E & e) {
		int index = indexOf(e);
		if (index == -1)
			throw NotSuchElementException();
		remove(index);
	}

    E & get(int index)
    {
        Node<E> * node = get_node(index);
        return node -> element;
    }

	int indexOf(const E & e) const 
    {

            Node<E> * node = head;
            Node<E> * preofnow = 0;
            Node<E> * temp = 0;
			I(i, _size)
            {
                if(node->element == e)
                return i;

                temp = node;
                node = node -> next_node(preofnow);
                preofnow = temp;
            }
            return EOF;
	}
private:
   	Node<E> * get_node(int index) const 
	{
		if (index < 0 || index >= _size) 
			throw NotSuchElementException();
		if (index < _size / 2) 
        {
			Node<E> * node = head;
            Node<E> * preofnow = 0;
            Node<E> * temp = 0;
			I(i, index)
            {
                temp = node;
                node = node -> next_node(preofnow);
                preofnow = temp;
            }
			return node;
		}
		else 
		{
			Node<E> * node = tail;
            Node<E> * nextofnow = 0;
            Node<E> * temp =0;
			I(i, (_size - 1 - index) )
            {
                temp = node;
                node = node -> prev_node(nextofnow);
                nextofnow = node;
            }
			return node;
		}
	}
