#include <stdexcept>
using namespace std;

#define I(i,n) for (int i = 0; i < n; ++ i)

class NoSuchKeyException {};

int circular_shift(int bits, int shift_bits) {
	const int num_bits = 8 * sizeof(int);
	shift_bits = shift_bits % num_bits;
	return bits << shift_bits | bits >> (num_bits - shift_bits);
}

int hash_function(const int & key) {
	int code = 0;
    code ^= circular_shift(key, 5);
	return code;
}

template <typename V>
class HT
{
	class Tuple
	{
	public:
		int key;
		V val;
		bool in_use;

		Tuple() : in_use(false) {}
	};

	vector<Tuple> tuples;
	int _size;

	int canonical_index(const int & key) const {
		int hash_code = hash_function(key); // needs hash_function(K)
		return hash_code % tuples.size();
	}

	// the returned tuple is either empty or with the same key
	int indexOfKey(const int & key) const {
		int index = canonical_index(key);
		while (true) { // will enter a dead loop if there is not empty tuple
			if (! tuples[index].in_use) return index;
			if (tuples[index].key == key) return index;
			index = (index + 1) % tuples.size();
		}
	}

public:
	HT() : _size(0) {
		tuples.resize(5000);
	}

	bool containsKey(const int & key) const {
		int index = indexOfKey(key);
		return tuples[index].in_use;
	}

	const V & operator [] (const int & key) const {
		int index = indexOfKey(key);
		if (! tuples[index].in_use)
			throw NoSuchKeyException();
		return tuples[index].val;
	}

	V & operator [] (const int & key) {
		int index = indexOfKey(key);
		if (! tuples[index].in_use)
			throw NoSuchKeyException();
		return tuples[index].val;
	}

	void put(const int & key, const V & val) {
		int index = indexOfKey(key);
		tuples[index].key = key;
		tuples[index].val = val;
		if (tuples[index].in_use) return;
		tuples[index].in_use = true;
		++ _size;
		// make sure empty > size / 2
		if (2 * _size > tuples.size())
			_double_tuples();
	}

	void _double_tuples() {
		vector<Tuple> non_empty_tuples;
		I(i, tuples.size())
			if (tuples[i].in_use)
				non_empty_tuples.push_back(tuples[i]);
		clear();
		tuples.resize(tuples.size() * 2);
		I(i, non_empty_tuples.size())
			put(non_empty_tuples[i].key, non_empty_tuples[i].val);
	}

	static bool _between(int hole, int canonical, int tuple_index) {
		if (hole < tuple_index)
			return canonical > hole && canonical <= tuple_index;
		else
			return canonical > hole || canonical <= tuple_index;
	}



	int size() const { return _size; }

	void clear() {
		I(i, tuples.size())
			tuples[i].in_use = false;
		_size = 0;
	}

	vector<int> getKeys()
	{
	    vector<int> keys;
	    for (int i = 0; i < tuples.size(); ++i)
        {
            if (tuples[i].in_use == true) keys.push_back(tuples[i].key);
        }
        return keys;
	}
		static bool _between(size_t hole, size_t canonical, size_t tuple_index) {
		if (hole < tuple_index)
			return canonical > hole && canonical <= tuple_index;
		else
			return canonical > hole || canonical <= tuple_index;
	}
		void remove(const int & key) { 
		size_t index = indexOfKey(key);
		if (! tuples[index].in_use) 
			throw NoSuchKeyException();
		tuples[index].in_use = false;
		-- _size;
		// if there is a hole between a tuple and its canonical position
		// move the tuple to the hole
		size_t hole = index;
		size_t tuple_index = hole;
		while (true) {
			tuple_index = (tuple_index + 1) % tuples.size();
			if (! tuples[tuple_index].in_use) return;
			size_t canonical = canonical_index(tuples[tuple_index].key);
			if (_between(hole, canonical, tuple_index)) continue;
			tuples[hole] = tuples[tuple_index];
			hole = tuple_index;
			tuples[hole].in_use = false;
		}
	}
};
