#include <iostream>
#include <sstream>
using namespace std;

int main() {
	int test_count;
	cin >> test_count;
	HT<string> map;
	for (int i = 0; i < test_count; ++ i) {
		stringstream ss;
		ss << "text" << (1 + i);
		map.put(i * 10, ss.str());
	}
	cout << map.size() << endl;
	for (int i = 0; i < test_count; ++ i) {
		if (! map.containsKey(i * 10)) {
			cout << "bad" << endl;
		}
		else if (i % 100 == 50) {
			cout << map[i * 10] << endl;
		}
	}
	vector<int> keys = map.getKeys();
	for (int i = 0; i < keys.size(); ++ i) {
		map.remove(keys[i]);
	}
	cout << map.size() << endl;
