#include<iostream>
#include<string>
#include<string.h>
#include<stdlib.h>
using namespace std;
const int N=1000;

class HT{
public:
	int Key[N];
	string value1[N];
	int value2[N];
	int ret;
	string rets;
	int Hash(string key)
	{
		return atoi(key.c_str());
	} 
	int _size;
	HT()
	{
		_size=0;
	}
	operator int()
	{
		return ret;
	};
	operator string() 
	{
		return rets;
	}
	void put(int key,string value)
	{
		++_size;
		Key[key]=1;//string
		value1[key]=value;
	}
	void put(int key,int value)
	{
		++_size;
		Key[key]=2;
		value2[key]=value;

	}
	void put(string key,int value)
	{
		++_size;
		int num=Hash(key);
		Key[num]=2;
		value2[num]=value;
	}
	void put(string key,string value)
	{
		++_size;
		int num=Hash(key);
		Key[num]=1;
		value1[num]=value;
	}
	int size()
	{
		return _size;
	}


	HT& operator[] (int key) //按照index重载[]
	{
		int index=key;
		if(Key[index]==1)
		{
			rets=value1[index];
		}
		else
		{
			ret=value2[index];
		}
		return *this;
	}
	HT& operator[] (string key) //按照index重载[]
	{
		int index=Hash(key);
		if(Key[index]==1)
		{
			rets=value1[index];
		}
		else
		{
			ret=value2[index];
		}
		return *this;
	}

};
