int main() {
	char arr[10][10];
	int i=0,j=0;
	for(i;i<10;++i) cin >> arr[i];
	for(i=0;i<9;i++){
		for(j=0;j<9-i;++j){
			if(arr[j][0]>arr[j+1][0]){
				char temp[10];
				strcpy(temp,arr[j]);
				strcpy(arr[j],arr[j+1]);
				strcpy(arr[j+1],temp);
			}
			else if(arr[j][0]==arr[j+1][0]){
				for (int k = 1; k < 10; ++ k){
					if(arr[j][k]!=arr[j+1][k]){
						if(arr[j][k]>arr[j+1][k]){
							char temp[10];
				    strcpy(temp,arr[j]);
				    strcpy(arr[j],arr[j+1]);
				    strcpy(arr[j+1],temp);
						}
						break;
					}
				} 
			}
		}
	}
	for(i=0;i<10;++i) cout<<arr[i]<<" ";
}

class Matrix5x5{
	public:
	     double arr[5][5];

	 Matrix5x5(){
	    for(int i=0;i<5;++i){
		   for(int j=0;j<5;++j){
			   arr[i][j]=0;
		   }
	   }
	 }

	 double get(int row,int col){
		return this->arr[row][col];
	 }

	 void set(int a ,int b ,double c){
		 this->arr[a][b]=c;
	 }

	 Matrix5x5 operator + (const Matrix5x5 & x){
		 Matrix5x5 temp;
		 for(int i=0;i<5;++i){
			 for(int j=0;j<5;++j){
				 temp.arr[i][j]=this->arr[i][j]+x.arr[i][j];
			 }
		 }
		 return temp;
	 }

};

