double & Tensor_get(int dimensions, const int sizes[],double data[], int x0, int x1, int x2, int x3) {
	if (dimensions == 1) {
		return data[x0];
	}
	else if (dimensions == 2) {
		return data[x1 * sizes[0] + x0];
	}
	else if (dimensions == 3) {
		return data[x1 * sizes[0] + x0 + x2 * sizes[0] * sizes[1]];
	}
	else {
		return data[x3 * sizes[0] * sizes[1] * sizes[2] + x2 * sizes[1] * sizes[0] + x1 * sizes[0] + x0];
	}	
}

