#include <iostream>
using namespace std;
class Matrix{
	private:
	   int rows;
	   int columns;
	   double *values;
	public:
	   Matrix(int rows, int columns, double values[]){
		   this->rows=rows;
		   this->columns=columns;
		   this->values=new double[rows*columns];
		   for (int i = 0; i < rows*columns; ++ i)  this->values[i]=values[i];
	   }

		Matrix(const Matrix & matrix2){
		   this->rows=matrix2.rows;
		   this->columns=matrix2.columns;
		   this->values=new double[rows*columns];
		   for (int i = 0; i < rows*columns; ++ i)  this->values[i]=matrix2.values[i];
		}

		~Matrix(){
			delete []values;
		}

		void print(){
			int k=0;
			for (int i = 0; i < this->rows; ++ i) {
				for (int j = 0; j < this->columns; ++ j)  cout << "    " <<values[k++];
				cout << "\n";
			}
		}

		Matrix concatenateRows(const Matrix & matrix2) const{
			double a_R[(this->rows+matrix2.rows)*matrix2.columns];
			for (int i = 0; i < this->rows*this->columns; ++ i)  a_R[i]=this->values[i];
			for (int i = this->rows*this->columns; i < (this->rows+matrix2.rows)*matrix2.columns; ++ i)  a_R[i]=matrix2.values[i-this->rows*this->columns];
			Matrix temp(this->rows+matrix2.rows , matrix2.columns ,a_R);
			return temp;
		}

		Matrix concatenateColumns(const Matrix & matrix2) const{
			double a_C[this->rows*(this->columns+matrix2.columns)];
			int k=0;
			for (int i = 0; i < this->rows; ++ i) {
				for (int j = 0; j < this->columns+matrix2.columns; ++ j){
					if(j<this->columns) a_C[k++]=this->values[this->columns*i+j];
					else  a_C[k++] = matrix2.values[matrix2.columns*i+j-matrix2.columns];
				} 
			}
			Matrix temp(this->rows ,this->columns+matrix2.columns ,a_C);
			return temp;
		}
};

#include <iostream>
using namespace std;
void Tensor_print(int dimensions, const int sizes[], const double data[]){
	if ( dimensions == 1){
		cout << "Tensor of "<<sizes[0]<<"\n";
		for (int i = 0; i < sizes[0]; ++ i)  cout << data[i] <<"\n";
	}

	else if (dimensions == 2){
		int k=0;
		cout << "Tensor of "<<sizes[0]<<"x"<<sizes[1]<<"\n";
		for (int i = 0; i < sizes[0]; ++ i) {
			for (int j = 0; j < sizes[1]; ++ j) cout << "    "<<data[k++];
			cout << "\n";
		}
	}

	else if(dimensions == 3){
		int k=0;
		cout << "Tensor of "<<sizes[0]<<"x"<<sizes[1]<<"x"<<sizes[2]<<"\n";
		for (int i = 0; i < sizes[0]; ++ i) {
			cout <<"data["<<i<<"]"<<"\n";
			for (int j = 0; j < sizes[1]; ++ j){
				for (int b = 0; b < sizes[2]; ++ b) cout <<"    "<< data[k++];
				cout << "\n";
			}
		}
	}

	else if(dimensions==4){
		int k=0;
		cout << "Tensor of "<<sizes[0]<<"x"<<sizes[1]<<"x"<<sizes[2]<<"x"<<sizes[3]<<"\n";
		for (int i = 0; i < sizes[0]; ++ i) {
			for (int j = 0; j < sizes[1]; ++ j){
				cout << "data[" << i <<"]["<< j <<"]" << "\n";
				for (int x = 0; x < sizes[2]; ++ x){
					for (int y = 0; y < sizes[3]; ++ y) cout <<"    " <<data[k++];
					cout << "\n";
				} 
			} 
		}
	}
}

#include <iostream>
using namespace std;

class Matrix{
	private:
	  int rows;
	  int columns;
	  double *values;
	public:
	  Matrix(int rows, int columns, double values[]){
		  this->rows=rows;
		  this->columns=columns;
		  this->values=new double[rows*columns];
		  for (int i = 0; i < rows*columns; ++ i)   this->values[i]=values[i];
	  }

	  Matrix(const Matrix & matrix2){
		  this->rows=matrix2.rows;
		  this->columns=matrix2.columns;
		  this->values=new double[rows*columns];
		  for (int i = 0; i < rows*columns; ++ i)  this->values[i]=matrix2.values[i];
	  }

	  ~Matrix(){
		  delete[]values;
	  }

	 void print(){
		  int k=0;
		  for (int i = 0; i < rows; ++ i) {
			  for (int j = 0; j < columns; ++ j)  cout << "    " << values[k++];
			  cout << "\n";
		  }
	  }

	  Matrix operator + (const Matrix & matrix2) const{
		  double value[rows*columns];
		  for (int i = 0; i < rows*columns; ++ i) value[i]=matrix2.values[i]+this->values[i];
		  Matrix temp(this->rows,this->columns,value);
		  return temp;
	  }

	  Matrix operator - (const Matrix & matrix2) const{
		  double value[rows*columns];
		  for (int i = 0; i < rows*columns; ++ i)  value[i]=this->values[i]-matrix2.values[i];
		  Matrix temp(this->rows,this->columns,value);
		  return temp;
	  }

	  Matrix operator + (double value) const{
		  double Value[rows*columns];
		  for (int i = 0; i < rows*columns; ++ i)  Value[i]=this->values[i]+value;
		  Matrix temp(this->rows,this->columns,Value);
		  return temp;
	  }

	  Matrix operator - (double value) const{
		  double Value[rows*columns];
		  for (int i = 0; i < rows*columns; ++ i)  Value[i]=this->values[i]-value;
		  Matrix temp(this->rows,this->columns,Value);
		  return temp;
	  }

};

