     double * values;

	public:

	  Matrix(int rows, int columns, double values[]){
		 this->rows=rows;
		 this->columns=columns;
		 this->values=new double[rows*columns];
		 for (int i = 0; i < rows*columns; ++ i)  this->values[i]=values[i];
	  }

	Matrix(const Matrix & matrix2){
		 this->rows=matrix2.rows;
		 this->columns=matrix2.columns;
		 this->values=new double[rows*columns];
		 for (int i = 0; i < rows*columns; ++ i)  this->values[i]=matrix2.values[i];		
	}

	void print(){
		int k=0;
		for (int i = 0; i < rows; ++ i){
			for (int j = 0; j < columns; ++ j) cout << "    "<<values[k++];
			cout << "\n";
		} 
	}
	  ~Matrix(){
		  delete [] values;
	  }

     Matrix max() const{
	  if(this->rows>1){
		 double arr[this->columns];
		 int k=0;
		 for (int i = 0; i < columns; ++ i) {
			 int Max=0;
			 for (int j = 0; j < rows; ++ j){
				 if(values[j*columns+i]>Max) Max=values[j*columns+i];
			 } 
			 arr[k++]=Max;
		 }
		 	  Matrix temp(1,this->columns,arr);
			  return temp;
	  }
		 else{
			 double arr[1];
			 int Max=0;
			 for (int i = 0; i < columns; ++ i)  if(values[i]>Max) Max=values[i];
			 arr[0]=Max;
			 Matrix temp(1,1,arr);
			 return temp;
		 }
	 }

	  Matrix min() const{
	  if(this->rows>1){
		 double arr[this->columns];
		 int k=0;
		 for (int i = 0; i < columns; ++ i) {
			 int Min=values[0];
			 for (int j = 0; j < rows; ++ j){
				 if(values[j*columns+i]<Min) Min=values[j*columns+i];
			 } 
			 arr[k++]=Min;
		 }
		 	  Matrix temp(1,this->columns,arr);
			  return temp;
	  }
		 else{
			 double arr[1];
			 int Min=values[0];
			 for (int i = 0; i < columns; ++ i)  if(values[i]<Min) Min=values[i];
			 arr[0]=Min;
			 Matrix temp(1,1,arr);
			 return temp;
		 }
	  }

	  Matrix sum() const{
		  if(this->rows>1){
		 double arr[this->columns];
		 int k=0;
      for(int i=0;i<columns;++i){
		  arr[k]=0;
		  for(int j=0;j<rows;++j) arr[k]=arr[k]+this->values[columns*j+i];
		  ++k;
	  }
		 Matrix temp(1,this->columns,arr); 
		 return temp;
	  }
		  else{
			  double sum[1];
			  sum[0]=0;
			  for (int i = 0; i < this->columns; ++ i)  sum[0]=sum[0]+this->values[i];
			  Matrix temp(1,1,sum);
			  return temp;
		  }
	  }


};

#include <iostream>
#include<cmath>
using namespace std;

class Matrix{
	private:
	  int rows;
     int columns;
     double * values;

	public:

	  Matrix(int rows, int columns, double values[]){
		 this->rows=rows;
		 this->columns=columns;
		 this->values=new double[rows*columns];
		 for (int i = 0; i < rows*columns; ++ i)  this->values[i]=values[i];
	  }

	Matrix(const Matrix & matrix2){
		 this->rows=matrix2.rows;
		 this->columns=matrix2.columns;
		 this->values=new double[rows*columns];
		 for (int i = 0; i < rows*columns; ++ i)  this->values[i]=matrix2.values[i];		
	}

	void print(){
		int k=0;
		for (int i = 0; i < rows; ++ i){
			for (int j = 0; j < columns; ++ j) cout << "    "<<values[k++];
			cout << "\n";
		} 
	}
	  ~Matrix(){
		  delete [] values;
	  }
	Matrix pow(double exponent){
		double arr[columns*rows];
		for (int i = 0; i < columns*rows; ++ i) arr[i]=1; 
		for (int i = 0; i < columns*rows; ++ i) {
			for (int j = 0; j < exponent; ++ j)  arr[i]=arr[i]*values[i];
		}
		Matrix temp(rows,columns,arr);
		return temp;
	}

   Matrix exp(){
			double arr[columns*rows];
			for (int i = 0; i < this->rows * this->columns; ++ i) {
				arr[i] = std::exp(this->values[i]);
			}
	      Matrix temp(this->rows, this->columns,arr);
			return temp;
	}

	Matrix log(){
			double arr[columns*rows];
			for (int i = 0; i < this->rows * this->columns; ++ i) {
				arr[i] = std::log(this->values[i]);
			}
	      Matrix temp(this->rows, this->columns,arr);
			return temp;	
	}

