template <typename T>

class Matrix{
	public:
		Matrix<T>(int a, int b) {
			this->rows = a;
			this->columns = b;
			for (int i = 0; i < a * b; ++ i) {
				this->values.push_back(0);
			}
		}

		Matrix<T>(int a, int b, vector<T> & v) {
			this->rows = a;
			this->columns = b;
			this->values = v;
		}

		void print() {
			for (int i = 0; i < rows; ++ i) {
				for (int j = 0; j < columns; ++ j) {
					cout << "    " << values[i * columns + j];
				}
				cout << endl;
			}
		}

		T & get(int a, int b) {
			return values[(a - 1) * columns + b - 1];
		}

		void operator=(Matrix<T> &other) {
			this->rows = other.rows;
			this->columns = other.columns;
			this->values = other.values;
		}

		Matrix<T> operator+(Matrix<T> &other) {
			Matrix<T> temp(this->rows, this->columns);
			for (int i = 0; i < this->rows * this->columns; ++ i) {
				temp.values[i] = this->values[i] + other.values[i];
			}
			return temp;
		}

		Matrix<T> operator+(T a) {
			Matrix<T> temp(this->rows, this->columns);
			for (int i = 0; i < this->rows * this->columns; ++ i) {
				temp.values[i] = this->values[i] + a;
			}
			return temp;
		}

		Matrix<T> operator-(Matrix<T> &other) {
			Matrix<T> temp(this->rows, this->columns);
			for (int i = 0; i < this->rows * this->columns; ++ i) {
				temp.values[i] = this->values[i] - other.values[i];
			}
			return temp;
		}

		Matrix<T> operator-(T a) {
			Matrix<T> temp(this->rows, this->columns);
			for (int i = 0; i < this->rows * this->columns; ++ i) {
				temp.values[i] = this->values[i] - a;
			}
			return temp;
		}

		Matrix<T> operator*(Matrix<T> & other) {
			Matrix<T> temp(this->rows, other.columns);
			for (int i = 0; i < this->rows; ++ i) {
				for (int j = 0; j < other.columns; j ++) {
					temp.values[i * other.columns + j] = 0;//
					for (int k = 0; k < this->columns; k ++) {
						temp.values[i * other.columns + j] = temp.values[i * other.columns + j] + this->values[k + i * this->columns] * other.values[k * other.columns + j];
					}
				}
			}
			return temp;
		}

		Matrix<T> operator*(T a) {
			Matrix<T> temp(this->rows, this->columns);
			for (int i = 0; i < this->rows * this->columns; ++ i) {
				temp.values[i] = this->values[i] * a;
			}
			return temp;
		}

		Matrix<T> getColumn(int a) {
			Matrix<T> temp(this->rows, 1);
			for (int i = 0; i < this->rows; ++ i) {
				temp.values[i] = this->values[i * this->columns + a - 1];
			}
			return temp;
		}

		Matrix<T> getRow(int a) {
			Matrix<T> temp(1, this->columns);
			for (int i = 0; i < this->columns; ++ i) {
				temp.values[i] = this->values[(a - 1) * this->columns + i];
			}
			return temp;
		}

		Matrix<T> reshape(int a, int b) {
			Matrix<T> temp(a, b);
			int index = 0;
			int cut = 0;
			for (int i = 0; i < b; ++ i) {
				for (int j = 0; j < a; ++ j) {
					temp.values[i + j * b] = this->values[index];
					if (index + this->columns < a * b) {
						index = index + this->columns;
					}
					else {
						cut ++;
						index = cut;
					}
				}
			}
			return temp;
		}

		Matrix<T> concatenateRows(Matrix<T> & other) {
			Matrix<T> temp(this->rows + other.rows, this->columns);
			for (int i = 0; i < this->rows * this->columns; ++ i) {
				temp.values[i] = this->values[i];
			} 
			for (int i = 0; i < other.rows * other.columns; ++ i) {
				temp.values[this->rows * this->columns + i] = other.values[i];
			}
			return temp;
		}

		Matrix<T> concatenateColumns(Matrix<T> & other) {
			Matrix<T> temp(this->rows, this->columns + other.columns);
			for (int i = 0; i < this->rows; ++ i) {
				for (int j = 0; j < this->columns; j ++) {
					temp.values[i * (this->columns + other.columns) + j] = this->values[i * this->columns + j];
				}
				for (int j = 0; j < other.columns; j ++) {
					temp.values[i * (this->columns + other.columns) + j + this->columns] = other.values[i * other.columns + j];
				}
			}
			return temp;
		}

		Matrix<T> transpose() {
			Matrix<T> temp(this->columns, this->rows);
			int index = 0;
			for (int i = 0; i < this->columns; ++ i) {
				for (int j = 0; j < this->rows; ++ j) {
					temp.values[i + j * this->columns] = this->values[index];
					index ++;
				}
			}
			return temp;
		}

		Matrix<T> max() {
			if(this->rows > 1){
				Matrix<T> temp(1, this->columns);
				for (int i = 0; i < this->columns; ++ i) {
					temp.values[i] = this->values[i];
					for (int j = 1; j < this->rows; ++ j) {
						if (temp.values[i] < this->values[j * this->columns + i]) {
							temp.values[i] = this->values[j * this->columns + i];
						}
					}
				} 
				return temp;
			}
			else{
				Matrix<T> temp(1, 1);
				temp.values[0] = this->values[0];
				for (int i = 1; i < this->columns; ++ i) {
					if (temp.values[0] < this->values[i]) {
						temp.values[0] = this->values[i];
					}
				}
				return temp;
			}
		}

		Matrix<T> min() {
			if(this->rows > 1){
				Matrix<T> temp(1, this->columns);
				for (int i = 0; i < this->columns; ++ i) {
					temp.values[i] = this->values[i];
					for (int j = 1; j < this->rows; ++ j) {
						if (temp.values[i] > this->values[j * this->columns + i]) {
							temp.values[i] = this->values[j * this->columns + i];
						}
					}
				} 
				return temp;
			}
			else{
				Matrix<T> temp(1, 1);
				temp.values[0] = this->values[0];
				for (int i = 1; i < this->columns; ++ i) {
					if (temp.values[0] > this->values[i]) {
						temp.values[0] = this->values[i];
					}
				}
				return temp;
			}
		}

		Matrix<T> sum() {
			if(this->rows > 1){
				Matrix<T> temp(1, this->columns);
				for (int i = 0; i < this->columns; ++ i) {
					for (int j = 0; j < this->rows; ++ j) {
						temp.values[i] = temp.values[i] + this->values[j * this->columns + i];
					}
				} 
				return temp;
			}
			else{
				Matrix<T> temp(1, 1);
				for (int i = 0; i < this->columns; ++ i) {
					temp.values[0] += this->values[i];
				}
				return temp;
			}
		}

	private:
		int rows;
		int columns;
		vector<T> values;
};

#include<cmath>
class Triangle : public GeometricObject{
	private:
	 double side1;
	 double side2;
	 double side3;
	public:
	 Triangle(){
		 side1=1.0;
		 side2=1.0;
		 side3=1.0;
	 }

	Triangle(double a, double b, double c){
		side1=a;
		side2=b;
		side3=c;
	}

	double getSide1(){
		return side1;
	}

	double getSide2(){
		return side2;
	}

	double getSide3(){
		return side3;
	}

	double getArea(){
		double s = (side1 + side2 + side3) / 2;
		double area = sqrt(s * (s - side1) * (s - side2) * (s - side3));
		return area;
	}

	double getPerimeter(){
		return side1+side2+side3;
	}
};

