#include <iostream>
#include <string>
#include <vector>
using namespace std;
vector<string> split(const string & line, const string & temp=" "){
	vector<string> words;
	int st=0;
	while(st < line.size()){
		int end=line.find(temp , st);
		int end2=end;
		while(line.find(temp , end2+1)==end2+1) ++end2;  
		if(st == end) st=st+temp.size();
		else{
			if(end == -1){
				words.push_back(line.substr(st));
			}
			words.push_back(line.substr(st,end-st));
			st=end2+1;
		}
	}
	return words;
}

class Point3D:public Point2D{
	private:
	  double x=0;
	  double y=0;
	  double z=0;
	public:
     Point3D(double x,double y,double z){
		 this->x=x;
		 this->y=y;
		 this->z=z;
	 }
	double getX(){
		return x;
	}
	double getY(){
		return y;
	}
	double getZ(){
		return z;
	}

	virtual double distance(Point2D & point2) {
		Point3D & a = dynamic_cast<Point3D &>(point2);
		double dx = x - a.getX();
		double dy = y - a.getY();
		double dz = z - a.getZ();
		return sqrt(dx * dx + dy * dy + dz*dz);
	}
};

