class Complex
{
	private:
		double m_real;
		double m_imag;
	public:
		Complex(double real, double imag)
		{
			this->m_real = real;
			this->m_imag = imag;
		}
		double real()
		{
			return this->m_real;
		}
		double imag()
		{
			return this->m_imag;
		}
		Complex operator + (Complex & c)
		{
			double num1 = this->m_real + c.m_real;
			double num2 = this->m_imag + c.m_imag;
			Complex com_com(num1, num2);
			return com_com;
		}
		Complex operator - (Complex & c)
		{
			double num1 = this->m_real - c.m_real;
			double num2 = this->m_imag - c.m_imag;
			Complex com_com(num1, num2);
			return com_com;
		}
		Complex operator * (Complex & c)
		{
			double num1 = this->m_real * c.m_real - this->m_imag * c.m_imag;
			double num2 = this->m_imag * c.m_real + this->m_real * c.m_imag;
			Complex com_com(num1, num2);
			return com_com;
		}
		Complex operator / (Complex & c)
		{
			double num1 = (this->m_real * c.m_real + this->m_imag * c.m_imag) / (c.m_real * c.m_real + c.m_imag * c.m_imag);
			double num2 = (this->m_imag * c.m_real - this->m_real * c.m_imag) / (c.m_real * c.m_real + c.m_imag * c.m_imag);
			Complex com_com(num1, num2);
			return com_com;
		}
		Complex operator - (double num)
		{
			double num1 = this->m_real - num;
			double num2 = this->m_imag;
			Complex com_com(num1, num2);
			return com_com;
		}
		Complex & operator += (Complex & c)
		{
			this->m_real = this->m_real + c.m_real;
			this->m_imag = this->m_imag + c.m_imag;
			return (*this);
		}
		Complex & operator -= (Complex & c)
		{
			this->m_real = this->m_real - c.m_real;
			this->m_imag = this->m_imag - c.m_imag;
			return (*this);
		}
		Complex & operator *= (Complex & c)
		{
			double r = this->m_real, i = this->m_imag;
			this->m_real = r * c.m_real - i * c.m_imag;
			this->m_imag = i * c.m_real + r * c.m_imag;
			return (*this);
		}
		Complex & operator /= (Complex & c)
		{
			double r = this->m_real, i = this->m_imag;
			this->m_real = (r * c.m_real + i * c.m_imag) / (c.m_real * c.m_real + c.m_imag * c.m_imag);
			this->m_imag = (i * c.m_real - r * c.m_imag) / (c.m_real * c.m_real + c.m_imag * c.m_imag);
			return (*this);
		}
};
Complex operator + (double num, Complex & c)
{
	double num1 = c.real() + num;
	double num2 = c.imag();
	Complex com_com(num1, num2);
	return com_com;
}
bool operator == (Complex & c1, Complex & c2)
{
	if(c1.real() == c2.real() && c1.imag() == c2.imag()) return true;
	return false;
}
bool operator != (Complex & c1,Complex & c2)
{
	if(c1 == c2) return false;
	return true;
}

