class Tensor
{
	private:
		int sizes[4];
		double * data;
		int num;

	public:
		Tensor(int a, int b = -1, int c = -1, int d = -1)
		{
			int count = 1;
			int sum = a;
			this->sizes[0] = a;
			if(b!= -1)
			{
				count ++;
				this->sizes[1] = b;
				sum *= b;
			}
			if(c != -1)
			{
				count ++;
				this->sizes[2] = c;
				sum *= c;
			}
			if(d!=-1)
			{
				count ++;
				this->sizes[3] = d;
				sum *= d;
			}
			this->num = count;
			data = new double[sum];
		}
		~Tensor()
		{
			delete[] data;
		}
		double & operator() (int a, int b = -1, int c = -1, int d =- 1)
		{
			int index = a;
			if(b != -1)
			{
				index += b * this->sizes[0];
			}
			if(c != -1)
			{
				index += c * this->sizes[0] * this->sizes[1];
			}if(d != -1)
			{
				index += d * this->sizes[0] * this->sizes[1] * this->sizes[2];
			}
			return data[index];
		}
};

