double Matrix:: get(int row, int column) const {
	if (row > rows || column > columns) {
		throw std::out_of_range("out_of_range");
	}
	return elements[(row - 1) * columns + column - 1];
}

void Matrix:: set(int row, int column, double value) {
	if (row > rows || column > columns) {
		throw std::out_of_range("out_of_range");
	}
	elements[(row - 1) * columns + column - 1] = value;
}

class MatrixSizesDoNotMatchException: public exception{ };

Matrix Matrix:: operator + (const Matrix & matrix2) const {
	if (this->rows != matrix2.rows || this->columns != matrix2.columns) {
		throw MatrixSizesDoNotMatchException();
	}
	Matrix temp(this->rows, this->columns);
	for (int i = 0; i < this->rows * this->columns; ++ i) {
		temp.elements[i] = this->elements[i] + matrix2.elements[i];
	}
	return temp;
}

