template <typename E>
void LinkedList<E>::removeAll(const E& e){
	int index = indexOf(e);
	if(index == -1)
		throw NotSuchElementException();
	while(index!=-1){
		remove(index);
		index = indexOf(e);
	}
}

private:
	Node<E> * get_node(int index)const{
		if(index<0||index>=_size) return 0;
		if(index<_size/2){
			Node<E> * node = head;
			Node<E> * prev_node = 0;
			I(i,index){
				Node<E> * use=prev_node;
				prev_node=node;
				node=node->next_node(use);
			}
			return node;
		}
		else {
			Node<E> * node = tail;
			Node<E> * next_node = 0;
			I(i,(_size-1-index)) {
				Node<E> * use = next_node;
				next_node=node;
				node=node->prev_node(use);
			}
			return node;
		}
	}
public:
	E & get(const int index)const{
		Node<E> * node = get_node(index);
		return node->element;
	}
	void set(const int index, const E & e){
		Node<E> * node = get_node(index);
		node->element = e;
	}
	E & getFirst()const{
        return get(0);
	}
	E & getLast()const{
        return get(_size-1);
	}
	void remove(int index){
		Node<E> * node = get_node(index);
		Node<E> * prev_node;
		Node<E> * next_node;
		if(index==0) prev_node = 0;
		else prev_node = get_node(index-1);
		if(index==_size-1) next_node = 0;
		else next_node = get_node(index+1);
		if(prev_node==0) head = next_node;
		if(next_node==0) tail = prev_node;
		if(prev_node!=0) prev_node->update_next_node(node,next_node);
		if(next_node!=0) next_node->update_prev_node(node,prev_node);
		delete node;
		_size--;
	}
	void removeFirst(){
		remove(0);
	}
	void removeLast(){
		remove(_size-1);
	}
	void add(int index, const E & e){
		Node<E> * node=new Node<E>();
		node->element = e;
		Node<E> * next_node = (index==_size ? 0 : get_node(index));
		Node<E> * prev_node = (next_node==0 ? tail : get_node(index-1));
		node->update_prev_and_next_node(prev_node,next_node);
		if(prev_node!=0) prev_node->update_next_node(next_node,node);
		if(next_node!=0) next_node->update_prev_node(prev_node,node);
		if(prev_node==0) head=node;
		if(next_node==0) tail=node;
		_size++;
	}
	void addFirst(const E & e){
		add(0,e);
	}
	void addLast(const E & e){
		add(_size,e);
	}
	void clear(){
		while(_size>0) removeFirst();
	}
	void addAll(const LinkedList & list, int index=-1){
		if(index==-1) index=_size;
		I(i,list._size){
			Node<E> * node = list.get_node(i);
			add(index+i,node->element);
		}
	}
	LinkedList(const LinkedList & list) : _size(0), head(0), tail(0){
        addAll(list);
	}
	LinkedList & operator = (const LinkedList & list){
		clear();
		addAll(list);
		return *this;
	}
	~LinkedList(){
		clear();
	}
	int indexOf(const E & e)const{
		Node<E> * node = head;
		I(i,_size-1){
			if(node->element==e) return i;
			node=get_node(i+1);
		}
		return -1;
	}
	int lastindexOf(const E & e) const{
		Node<E> * node=tail;
		I(i,_size-1){
			if(node->element == e) return i;
			node=get_node(_size-i-2);
		}
		return -1;
	}
	bool contains(const E & e)const{
		return indexOf(e)>=0;
	}
	void removeFirstOccurrence(const E & e){
		int index=indexOf(e);
		if(index==-1) throw NotSuchElementException();
		remove(index);
	}
	void removeLastOccurrence(const E & e){
		int index=lastindexOf(e);
		if(index=-1) throw NotSuchElementException();
		remove(index);
	}

