#include <iostream>
#include <fstream>
#include <vector>
#include <string>
#include <stdexcept>
using namespace std;

#define I(i,n) for (size_t i = 0; i < n; ++ i)
#define HT HashTable
#define K int
class NoSuchKeyException {};

size_t circular_shift(size_t bits, int shift_bits) {
	const int num_bits = 8 * sizeof(size_t);
	shift_bits = shift_bits % num_bits;
	return bits << shift_bits | bits >> (num_bits - shift_bits);
}

size_t hash_function(const int & key) {
	size_t code = 0;
	I(i, key)
		code ^= circular_shift(key, i * 5);
	return code;
}

template <typename V>
class HashTable
{
	class Tuple
	{
	public:
		K key;
		V val;
		bool in_use;

		Tuple() : in_use(false) {}
	};

	vector<Tuple> tuples;
	size_t _size;

	size_t canonical_index(const K & key) const {
		size_t hash_code = hash_function(key); // needs hash_function(K)
		return hash_code % tuples.size();
	}

	// the returned tuple is either empty or with the same key
	size_t indexOfKey(const K & key) const {
		size_t index = canonical_index(key);
		while (true) { // will enter a dead loop if there is not empty tuple
			if (! tuples[index].in_use) return index;
			if (tuples[index].key == key) return index;
			index = (index + 1) % tuples.size();
		}
	}

public:
	HashTable() : _size(0) {
		tuples.resize(2);
	}
    vector <int> getKeys(){
		vector<int> result;
		for(int i=0;i<tuples.size();i++){
			result.push_back(tuples[i].key);
		}
		return result;
	}
	bool containsKey(const K & key) const {
		size_t index = indexOfKey(key);
		return tuples[index].in_use;
	}

	const V & operator [] (const K & key) const {
		size_t index = indexOfKey(key);
		if (! tuples[index].in_use) 
			return tuples[index].val;
		return tuples[index].val;
	}

	V & operator [] (const K & key) {
		size_t index = indexOfKey(key);
		if (! tuples[index].in_use) 
			return tuples[index].val;
		return tuples[index].val;
	}

	void put(const K & key, const V & val) {
		size_t index = indexOfKey(key);
		tuples[index].key = key;
		tuples[index].val = val;
		if (tuples[index].in_use) return;
		tuples[index].in_use = true;
		++ _size;
		// make sure empty > size / 2
		if (2 * _size > tuples.size())
			_double_tuples();
	}

	void _double_tuples() {
		vector<Tuple> non_empty_tuples;
		I(i, tuples.size())
			if (tuples[i].in_use)
				non_empty_tuples.push_back(tuples[i]);
		clear();
		tuples.resize(tuples.size() * 2);
		I(i, non_empty_tuples.size())
			put(non_empty_tuples[i].key, non_empty_tuples[i].val);
	}

	static bool _between(size_t hole, size_t canonical, size_t tuple_index) {
		if (hole < tuple_index)
			return canonical > hole && canonical <= tuple_index;
		else
			return canonical > hole || canonical <= tuple_index;
	}

	void remove(const K & key) { 
		size_t index = indexOfKey(key);
		if (! tuples[index].in_use) 
			return ;
		tuples[index].in_use = false;
		-- _size;
		// if there is a hole between a tuple and its canonical position
		// move the tuple to the hole
		size_t hole = index;
		size_t tuple_index = hole;
		while (true) {
			tuple_index = (tuple_index + 1) % tuples.size();
			if (! tuples[tuple_index].in_use) return;
			size_t canonical = canonical_index(tuples[tuple_index].key);
			if (_between(hole, canonical, tuple_index)) continue;
			tuples[hole] = tuples[tuple_index];
			hole = tuple_index;
			tuples[hole].in_use = false;
		}
	}

	size_t size() const { return _size; }

	void clear() {
		I(i, tuples.size())
			tuples[i].in_use = false;
		_size = 0;
	}

	template <typename V2>
	friend ostream & operator << (ostream & out, const HashTable<V2> & dict);

	template <typename V2>
	friend istream & operator >> (istream & in, HashTable<V2> & dict);

	void _inspect() const {
		I(i, tuples.size())
			if (tuples[i].in_use)
				cout << "#" << i << " C" << canonical_index(tuples[i].key) 
					<< " " << tuples[i].key << " " << tuples[i].val << endl;
	}
};

template <typename V>
ostream & operator << (ostream & out, const HashTable<V> & dict) {
	out << dict.size() << endl;
	I(i, dict.tuples.size())
		if (dict.tuples[i].in_use)
			out << dict.tuples[i].key << "\t" << dict.tuples[i].val << endl;
	return out;
}

template <typename V>
istream & operator >> (istream & in, HashTable<V> & dict) {
	dict.clear();
	size_t size;
	in >> size;
	I(i, size) {
		K key;
		V val;
		in >> key >> val;
		dict.put(key, val);
	}
	return in;
}

