int sort(char * a,char * b){
	int i;
	int la = strlen(a);
	int lb = strlen(b);
	int minlen;
	if(la >= lb) minlen = lb;
	else minlen = la;
	for(i = 0;i < minlen;i ++){

		if(a[i] < b[i] || a[i] == ' '){
			return 0;
			break;
		}
		if(a[i] > b[i] || b[i] == ' '){
			return 1;
			break;
		}
		if(a[i] == b[i]) continue;
	}
}

void swap(char *&a,char* &b) {
	char *temp;
	temp=a;
	a=b;
	b=temp;
}

int main() {
	char s[10][50];
	int j = 0,i = 0;
	int n = 10;
	while (n --){
		cin >> s[n];	
	}
	for (i = 0; i < 10; ++ i) {
		for (j = i+1; j < 10; ++ j){
			if(sort(s[i],s[j]) == 1){
				swap(s[i],s[j]);
			}
		} 
	}
	for (i = 0; i < 10; ++ i) {
		cout << s[i] << ' ';	
	}
}

