		for (int i = 0; i < row ; ++ i) {
			for (int j = 0; j < column; ++ j){
				cout << "    " << this->value[i*column+j] ;
			} 
			cout << endl;
		}
	}
	Matrix getRow(int row){
		double values[this->column];
		for (int i = 0; i < this->column; ++ i){
			int temp = this->column * (row - 1) + i;
			values[i] = this->value[temp];
		} 
		Matrix result(1,this->column,values);
		return result;
	}
	Matrix getColumn(int column){
		double values[this->row];
		for (int i = 0; i < this->row; ++ i){
			int temp = i * this->column + column - 1;
			values[i] = this->value[temp];
		} 
		Matrix result(this->row,1,values);
		return result;
	}
	~Matrix(){
		delete []value;
	}
};

