class Matrix
{
private:
	int row,column;
	double * value;
public:
	Matrix(int row, int column,double values[]){
		this->row = row;
		this->column = column;
		this->value = new double[row*column];
		for (int i = 0; i < row*column; ++ i){
			this->value[i] = values[i];
		}
	}
	void print(){
		for (int i = 0; i < row ; ++ i) {
			for (int j = 0; j < column; ++ j){
				cout << "    "<< this->value[i*column+j] ;
			} 
			cout << endl;
		}
	}
	Matrix(const Matrix & matrix2){
		this->row = matrix2.row;
		this->column = matrix2.column;
		this->value = new double [row*column];
		for (int i = 0; i < row*column; ++ i){
			this->value[i] = matrix2.value[i];
		} 
	}
	~Matrix(){
		delete []value;
	}
	Matrix concatenateRows(const Matrix & matrix2){
		double values[row*column*2];
		for (int i = 0; i < this->row*this->column*2; ++ i) {
			if(i < this->row*this->column) values[i] = this->value[i];
			else values[i] = matrix2.value[i-row*column];
		}
		Matrix temp(2*this->row, this->column, values);
		return temp;
	}
	Matrix concatenateColumns(const Matrix & matrix2){
		double values[row*column*2];
		for (int i = 0; i < 2*this->row*this->column; ++ i) {
			int temp = i / this->column;
			if(temp%2==0){
				int jian = temp / 2;
				values[i] = this->value[i-jian*this->column];
			} 
			else{
				int jian = (temp+1) / 2;
				values[i] = matrix2.value[i-jian*this->column];
			} 
		}
		Matrix temp(this->row, 2*this->column, values);
		return temp;
	}
};

#include <iostream>
using namespace std;

void Tensor_print(int dimensions, const int sizes[], const double data[]){
	if(dimensions == 1){
		cout << "Tensor of " << sizes[0] << endl;
		for (int i = 0; i < sizes[0]; ++ i) {
			cout << "    " << data[i] << endl;
		}
	}
	if(dimensions == 2){
		cout << "Tensor of " << sizes[0] << 'x' << sizes[1] << endl;
		for (int i = 0; i < sizes[0]; ++ i) {
			for (int j = 0; j < sizes[1]; ++ j){
				cout << "    " << data[i*sizes[1]+j];
			} 
			cout << endl;
		}
	}
	if(dimensions == 3){
		cout << "Tensor of " << sizes[0] << 'x' << sizes[1] << 'x' << sizes[2] << endl;
		for(int k = 0;k < sizes[0];k ++){
			cout << "data[" << k << ']' << endl;
			for (int i = 0; i < sizes[1]; ++ i) {
				for (int j = 0; j < sizes[2]; ++ j){
					cout << "    " << data[i*sizes[2]+j];
				} 
				cout << endl;
			}
		}
	}
	if(dimensions == 4){
		cout << "Tensor of " << sizes[0] << 'x' << sizes[1] << 'x' << sizes[2] << 'x' << sizes[3] << endl;
		for (int n = 0; n < sizes[0]; ++ n) {
			for(int k = 0;k < sizes[1];k ++){
				cout << "data[" << n << ']' << '[' << k << ']' << endl;
				for (int i = 0; i < sizes[2]; ++ i) {
					for (int j = 0; j < sizes[3]; ++ j){
						cout << "    " << data[i*sizes[3]+j];
					} 
					cout << endl;
				}
			}
		}
	}
}

