class Matrix{
	private:
		int rows;
		int columns;
		double * values;
	public:
        Matrix(int rows, int columns){
			this->rows = rows;
			this->columns = columns;
			double * p = NULL;
			p = new double[rows*columns];
			for(int i=0;i<rows*columns;i++){
				p[i]=0;
			}
			this->values=p;
		}
		Matrix(int rows, int columns,double values[]){
			this->rows = rows;
			this->columns = columns;
			double * p = 0;
			p = new double[rows*columns];
			for(int i=0;i<rows*columns;i++){
				p[i]= values[i];
			}
			this->values=p;
		}
		~Matrix(){
			delete [] this->values;
		}
	    void set(int row, int column, double value){
			this->values[(row-1)*this->columns+column-1]=value;
		}
		Matrix& operator=(Matrix &Matrix1){
			if(this!=&Matrix1){
				if(values!=NULL){
					delete [] values;
					values = NULL;
				}
				rows=Matrix1.rows;
				columns=Matrix1.columns;
				values = new double[rows*columns];
				for(int i=0;i<rows*columns;i++){
					values[i]=Matrix1.values[i];
				}
			}
			return *this;
		}
		void print(){
			for(int i=1;i<rows*columns+1;i++){
				if(i % columns == 0){
					cout <<"    "<< this->values[i-1] << endl;
				}
				else cout<<"    "<< this->values[i-1] ;
			}
		}
};

