		this->row = matrix2.row;
		this->column = matrix2.column;
		this->value = new double [row*column];
		for (int i = 0; i < row*column; ++ i){
			this->value[i] = matrix2.value[i];
		} 
	}
	~Matrix(){
		delete []value;
	}
	Matrix max() const{
		double judge = 0;
		int count = 0;
		if(this->row > 1){
			double * temp = new double[this->column];
			for(int j = 0;j < this->column;++ j){
				for (int i = 0; i < this->row; ++ i) {
					if(judge < this->value[i*this->column+j]) {
						judge = this->value[i*this->column+j];
						count = i*this->column+j;
					}
				}
				temp[j] = this->value[count];
				judge = 0;
			}
			Matrix res(1,this->column,temp);
			delete []temp;
			return res;
		}
		else{
			for (int i = 0; i < this->column; ++ i){
				if(judge < this->value[i]){
					judge = this->value[i];
					count = i;
				}
			} 
			double temp[1] = {0};
			temp[0] = judge;
			Matrix res(1,1,temp);
			return res;
		}
	}
	Matrix min() const{
		double judge = 1000;
		int count = 0;
		if(this->row > 1){
			double * temp = new double[this->column];
			for(int j = 0;j < this->column;++ j){
				for (int i = 0; i < this->row; ++ i) {
					if(judge > this->value[i*this->column+j]) {
						judge = this->value[i*this->column+j];
						count = i*this->column+j;
					}
				}
				temp[j] = this->value[count];
				judge = 1000;
			}
			Matrix res(1,this->column,temp);
			delete []temp;
			return res;
		}
		else{
			for (int i = 0; i < this->column; ++ i){
				if(judge > this->value[i]){
					judge = this->value[i];
					count = i;
				}
			} 
			double temp[1] = {0};
			temp[0] = judge;
			Matrix res(1,1,temp);
			return res;
		}
	}
