		double sumed = 0;
		if(this->row > 1){
			double * temp = new double[this->column];
			for(int j = 0;j < this->column;++ j){
				for (int i = 0; i < this->row; ++ i) {
					sumed += this->value[i*this->column+j];
				}
				temp[j] = sumed;
				sumed = 0;
			}
			Matrix res(1,this->column,temp);
			delete []temp;
			return res;
		}
		else{
			for (int i = 0; i < this->column; ++ i){
				sumed += this->value[i];
			} 
			double temp[1] = {0};
			temp[0] = sumed;
			Matrix res(1,1,temp);
			return res;
		}
	}
