			this->rows = row;
			this->columns = column;
			this->values = new T[rows*columns];
			memset(values,0,rows*columns*sizeof(T));
		}
		Matrix(int row, int column,const T value[]){
			this->rows = row;
			this->columns = column;
			this->values = new T[rows*columns];
			for(int i=0;i<rows*columns;i++){
				values[i] = value[i];
			}
		}
		Matrix(const Matrix & matrix2){
			this->rows = matrix2.rows;
			this->columns = matrix2.columns;
			this->values = new T [rows*columns];
			for (int i = 0; i < rows*columns; ++ i){
				this->values[i] = matrix2.values[i];
			} 
		}
		~Matrix(){
			delete [] this->values;
		}
		Matrix & operator = (const Matrix & matrix2){
			if(values!=0)delete []values;
			rows = matrix2.rows;
			columns = matrix2.columns;
			values = new T[rows*columns];
			for (int i = 0; i < rows*columns; ++ i){
				values[i] = matrix2.values[i];
			} 
