#include <iostream>
#include <math.h>
using namespace std;
class Triangle{
public:
	double side1,side2,side3;
	string color;
	Triangle(){
		side1 = side2 = side3 = 1.0;
	}
	Triangle(double s1,double s2, double s3){
		side1 = s1;
		side2 = s2;
		side3 = s3;
	}
	void setColor(string color2){
		color = color2;
	}
	string getColor(){
		return color;
	}
	double getSide1(){ return side1; }
	double getSide2(){ return side2; }
	double getSide3(){ return side3; }
	double getArea(){
		double s = (side3 + side2 + side1)/2;
		double res = sqrt(s * (s-side3) * (s-side2) * (s-side1) );
		return res;
	}
	double getPerimeter(){
		return side3 + side2 + side1;
	}
};

#include <iostream>
#include <string>
#include <vector>
using namespace std;

vector<string> split(const string &s) {
   vector<string> words;
	int st = 0;
	while(st < s.size()){
		int end = s.find(' ',st);
		if(end == st) st += 1;
		else{
			if(end == -1){
				words.push_back(s.substr(st));
				break;
			}
			words.push_back(s.substr(st, end-st));
			st = end + 1;		
		}
	}
	return words;
}

