class Point3D: public Point2D
{
private:
	double x;
	double y;
	double z;
public:
	Point3D(){
		x = y = z = 0;
	}
	Point3D(double a,double b,double c){
		x = a;
		y = b;
		z = c;
	}
	double getX() {
		return x;
	}
	double getY() {
		return y;
	}
	double getZ(){
		return z;
	}
	virtual double distance(Point2D & point2) {
		Point3D & temp = dynamic_cast<Point3D &>(point2);
		double dx = x - temp.x;
		double dy = y - temp.y;
		double dz = z - temp.z;
		return sqrt(dx * dx + dy * dy + dz * dz);
	}

};

