using namespace std;

class Full : public Matrix{
private:
	int columns;
	int rows;
	double * values;
public:
	Full(int row, int column){
		rows = row;
		columns = column;
		values = new double [row * column];
		for (int i = 0; i < rows*columns; ++ i) {
			values[i] = 0;
		}
	}
	Full(int row, int column, double * value){
		rows = row;
		columns = column;
		values = new double [row * column];
		for (int i = 0; i < rows*columns; ++ i) {
			values[i] = value[i];
		}
	}
	Full(const Matrix & matrix2){
		rows = matrix2.size(1);
		columns = matrix2.size(2);
		values = new double [rows * columns];
		for (int i = 0; i < rows; ++ i) {
			for (int j = 0; j < columns; ++ j) {
				values[i*columns+j] = matrix2.get(i+1,j+1);
			}
		}
	}
	~Full(){
		delete [] values;
	}
	int size(int dimension) const {
		if(dimension != 1 && dimension != 2) 
			return 0;
		if(dimension == 1)
			return rows; 
		if(dimension == 2)
			return columns;
	}

	void set(int row, int column,const double value){
		values[(row-1)*columns+column-1] = value;
	}

	double get(int row, int column) const{
		return values[(row-1)*columns+column-1];
	}

	void print() const {
		for (int i = 0; i < rows; ++ i) {
			for (int j = 0; j < columns; ++ j) {
				cout << "    " <<values[i * columns + j];
			}
			cout << endl;
		}
	}

	Matrix & operator = (const Matrix & matrix2){
		if(values != 0){
			delete [] values;
		}
		rows = matrix2.size(1);
		columns = matrix2.size(2);
		values = new double [rows * columns];
		for (int i = 0; i < rows; ++ i) {
			for (int j = 0; j < columns; ++ j) {
				values[i*columns+j] = matrix2.get(i+1,j+1);
			}
		}
		return * this;
	}
};

