#include <iostream>
using namespace std;
class MyTensor: public Tensor<double>
{
private:
	vector<double> values;
	int size;
public:
	MyTensor(vector<int> sizes) : Tensor(sizes){
		size = sizes.size();
		int space = 1;
		for (int i = 0; i < size; ++ i) {
			space = space * sizes[i];
		}
		for (int i = 0; i < space; ++ i) {
			values.push_back(0);
		}
	}
	~MyTensor(){}
	double & get(const vector<int> & indexes)
	{
		int dim = indexes.size();
		if(dim == 1)return values[indexes[0] - 1];
		if(dim == 2)return values[indexes[1]*sizes[0] + indexes[0] - 1];
		if(dim == 3)return values[indexes[2]*sizes[1]*sizes[0] + indexes[1]*sizes[0] + indexes[0] - 1];
		if(dim == 4)return values[indexes[3]*sizes[2]*sizes[1]*sizes[0] + indexes[2]*sizes[1]*sizes[0] + indexes[1]*sizes[0] + indexes[0] - 1];
	}
};

class Circle{
private:
	double radius;
public:
	int operator<(const Circle & circle2){
		if(radius < circle2.radius) return 1;
		return 0;
	}
	int operator<=(const Circle & circle2){
		if(radius <= circle2.radius) return 1;
		return 0;
	}	
	int operator==(const Circle & circle2){
		if(radius == circle2.radius) return 1;
		return 0;
	}
	int operator!=(const Circle & circle2){
		if(radius != circle2.radius) return 1;
		return 0;
	}
	int operator>(const Circle & circle2){
		if(radius > circle2.radius) return 1;
		return 0;
	}
	int operator>=(const Circle & circle2){
		if(radius >= circle2.radius) return 1;
		return 0;
	}
	Circle(double value){
		radius = value;
	}
	~Circle(){

	}
};

class I
{
	private:
		int a,b,c,d;
		vector<int> vals;
	public:
		I(int a=-1,int b = -1,int c = -1, int d = -1 ):a(a),b(b),c(c),d(d)
		{
			if(a!=-1)vals.push_back(a);	
			if(b!=-1)vals.push_back(b);
			if(c!=-1)vals.push_back(c);
			if(d!=-1)vals.push_back(d);
		}
		operator vector<int>()const{
			return vals;
		}
};

#include<iostream>
using namespace std;

class Complex{
	private:
		double r,i;
	public:
		Complex(double r,double i):r(r),i(i){};
		Complex():r(0),i(0){};
		Complex operator-(const Complex &other){
			double r=this->r-other.r;
			double i=this->i-other.i;
			return Complex(r,i);
		}
		Complex &operator=(const Complex &other){
			this->r=other.r;
			this->i=other.i;
			return *this;
		}
		 double get_r()const{return r;}
		 double get_i()const{return i;}
};
Complex operator+(const Complex&c1,const Complex &c2){
			double r=c1.get_r()+c2.get_r();
			double i=c1.get_i()+c2.get_i();
