		}
};

#include <iostream>
#include<math.h>
using namespace std;
class Complex {
	private:
		double r,i;
    public: 
    	double real()const{
    		return r;
		}
		double imag()const{
			return i;
		}
    	friend Complex operator+(const Complex &c1,const Complex&c2);
    	friend Complex operator*(const Complex &c1,const Complex&c2);
    	friend Complex operator-(const Complex &c1,const Complex&c2);
    	friend Complex operator/(const Complex &c1,const Complex&c2);
		Complex(double r_,double i_):r(r_),i(i_){}

		Complex& operator += (const Complex c2)
		{
			this->r += c2.r;
			this->i += c2.i;
			return *this;
		}
		Complex& operator -= (const Complex c2)
		{
			this->r -= c2.r;
			this->i -= c2.i;
			return *this;
		}
		Complex& operator *= (const Complex c2)
		{
			double rr = this->r * c2.r - this->i * c2.i;
			double ii = this->r * c2.i + this->i * c2.r;
			this->r = rr;
			this->i = ii;
			return *this;
		}
		Complex& operator /= (const Complex c2)
		{
			double rr = this->r * c2.r + this->i * c2.i;
			double ii = -this->r * c2.i + this->i * c2.r;
			double div = c2.r * c2.r + c2.i * c2.i;
			this->r = rr/div;
			this->i = ii/div;
			return *this;
		}
		bool operator==(const Complex &c2){
			return this->r==c2.r&&this->i==c2.i;
		}
		bool operator!=(const Complex &c2){
			return this->r!=c2.r||this->i!=c2.i;
		}
		Complex& operator() (){
			return *this;
		}
		Complex(int r):r(r),i(0){

		}
};

Complex operator+(const Complex &c1,const Complex &c2){
	double r=c1.real()+c2.real();
	double i=c1.imag()+c2.imag();
	return Complex(r,i);
}
Complex operator-( const Complex &c1,const  Complex &c2){
	double r=c1.real()-c2.real();
	double i=c1.imag()-c2.imag();
	return Complex(r,i);
}
Complex operator*(const Complex &c1, const Complex &c2){
	double r=c1.real()*c2.real()-c1.imag()*c2.imag();
	double i=c1.real()*c2.imag()+c1.imag()*c2.real();
	return Complex(r,i);
}
Complex operator/(const Complex &c1, const Complex &c2){
	double r=(c1.r*c2.r+c1.i*c2.i)/(pow(c2.r,2)+pow(c2.i,2));
	double i=(c1.i*c2.r-c1.r*c2.i)/(pow(c2.r,2)+pow(c2.i,2));
	return Complex(r,i);
}

