using namespace std;

class Tensor {

	vector<double>values;

	int total;
public:
	vector<int>sizes;
	int dimension;
	Tensor() :dimension(0) {};
	Tensor(vector<int>_sizes) {
		dimension = _sizes.size();
		total = 1;
		for (int i = 0; i < dimension; ++i) {
			sizes.push_back(_sizes[i]);
			total *= _sizes[i];
		}
		for (int i = 0; i < total; ++i) values.push_back(0);
	};
	double& operator()(int a = -1, int b = -1, int c = -1, int d = -1) {
		vector<int>temp;
		if (a != -1) temp.push_back(a);
		if (b != -1) temp.push_back(b);
		if (c != -1) temp.push_back(c);
		if (d != -1) temp.push_back(d);
		return values[_index(temp)];
	}
	const double& operator()(int a = -1, int b = -1, int c = -1, int d = -1)const {
		vector<int>temp;
		if (a != -1) temp.push_back(a);
		if (b != -1) temp.push_back(b);
		if (c != -1) temp.push_back(c);
		if (d != -1) temp.push_back(d);
		return values[_index(temp)];
	}
	int _index(const vector<int>& indexes) const {
		int index = 0;
		for (int i = 0; i < indexes.size(); ++i) {
			int temp = indexes[i];
			for (int j = i + 1; j < sizes.size(); ++j) temp *= sizes[j];
			index += temp;
		}
		return index;
	}
	int get_total() {
		return total;
	}
	Tensor& operator=(const vector<double>& val) {
		for (int i = 0; i < val.size(); ++i) values[i] = val[i];
		return *this;
	}
};

istream& operator>>(istream& in, Tensor& c) {
	int dimension;
	in >> dimension;
	vector<int>sizes;
	int p;
	for (int i = 0; i < dimension; ++i) {
		in >> p;
		sizes.push_back(p);
	}
	Tensor T(sizes);
	vector<double>val;
	double temp;
	for (int i = 0; i < T.get_total(); ++i) {
		in >> temp;
		val.push_back(temp);
	}
	T = val;
	c = T;
	return in;
}
ostream& operator<<(ostream& out, const Tensor& c) {
	out << c.dimension << endl;
	for (int i = 0; i < c.sizes.size(); ++i) out << c.sizes[i] << " ";
	out << endl<<endl;
	if (c.dimension == 3) {
		for (int i = 0; i < c.sizes[0]; ++i) {
			for (int j = 0; j < c.sizes[1]; ++j) {
				for (int k = 0; k < c.sizes[2]; ++k) {
					out  << c(i, j, k) << " ";
				}
				out << endl;
			}
			out << endl;
		}
	}
	if (c.dimension == 4) {
		int flag=true;
		for (int i = 0; i < c.sizes[0]; ++i) {
			for (int j = 0; j < c.sizes[1]; ++j) {
				for (int k = 0; k < c.sizes[2]; ++k) {
					for (int p = 0; p < c.sizes[3]; ++p) {
