#include <iostream>
#include <vector>
using namespace std;

class MatrixSizesDoNotMatchException{};

double Matrix:: get(int row, int column) const
{
	if(row>this->size(1)||column>this->size(2)) throw out_of_range("");
	row--;
	column--;
	return this->elements[row*this->columns+column];
}

void Matrix:: set(int row, int column, double value)
{
	if(row>this->rows||column>this->columns) throw out_of_range("");
	--row;
	--column;
	this->elements[row*this->columns+column] = value;
	return;
}

Matrix Matrix:: operator+(const Matrix&matrix2)const{
	if(this->rows!=matrix2.rows||this->columns!=matrix2.columns)throw MatrixSizesDoNotMatchException();
	Matrix ans(this->rows,this->columns);
	vector<double> eles;
	for (int i = 0; i < this->rows*this->columns; ++ i) 
		eles.push_back(this->elements[i] + matrix2.elements[i]);
	for(int i=1;i<=this->size(1);++i){
		for(int j=1;j<=this->size(2);++j){
			ans.set(i,j,eles[(i-1)*this->columns+j-1]);
		}
	}
	return ans;
}

